
public class PatternExample {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
       int i,j;
       for(i=0;i<5;i++){
    	   for(j=0;j<=i;j++)
    	   {
    		   System.out.println("* ");
    	   }
          System.out.println();
       }
       
	}

}